[x y] = meshgrid(-6:0.1:6,-6:0.1:6); a = sinc(x).*sinc(y); lMaxInd = localMaximum(a,[20 20]); lMinInd = localMaximum(-a,[20 20]); figure; mesh(x,y,a); hold on; plot3(x(lMaxInd),y(lMaxInd),a(lMaxInd),'k*','markersize',10,'linewidth',1.5); plot3(x(lMinInd),y(lMinInd),a(lMinInd),'g*','markersize',10','linewidth',1.5); legend('sinc(x)sinc(y)','peaks','valleys','location','best')